import React from 'react';
import CartItems from '../components/CartItems';
import CartTotal from '../components/CartTotal';

const CartPage = () => {
  return (
    // Parent container with horizontal centering and padding for some spacing
    <div className='flex flex-col md:flex-row justify-center items-start w-full px-4 py-8'>
      {/* CartItems with conditional width and margin auto for centering */}
      <div className="w-full md:w-auto mb-4 md:mb-0 mx-auto">
        <CartItems />
      </div>
      {/* CartTotal with conditional width and margin auto for centering */}
      <div className="w-full md:w-auto mx-auto">
        <CartTotal />
      </div>
    </div>
  );
}

export default CartPage;
