const CartPage = () => {
  return (
    // Use `justify-center` to center the children horizontally in the flex container
    // If you want the items to align from the top, you might not need `items-center`
    <div className='flex flex-col md:flex-row justify-center items-start w-full'>
      <div className="w-full md:flex-1 px-4">
        <CartItems />
      </div>
      <div className="w-full md:w-2/5 lg:w-3/5 xl:w-4/5 px-4 mt-4 md:mt-0">
        <CartTotal />
      </div>
    </div>
  );
}

export default CartPage;
