import React from 'react';
import CartItems from '../components/CartItems';
import CartTotal from '../components/CartTotal';

const CartPage = () => {
  return (
    // Full-width outer container
    <div className='flex justify-center w-full'>
      // Centered inner container with auto horizontal margins
      <div className="flex flex-col md:flex-row items-start md:items-center mx-auto px-4">
        <div className="w-full md:flex-1">
          <CartItems />
        </div>
        <div className="w-full md:w-2/5 lg:w-3/5 xl:w-4/5 mt-4 md:mt-0">
          <CartTotal />
        </div>
      </div>
    </div>
  );
}

export default CartPage;
