import React, { useContext } from "react";
import { CartContext } from "../components/CartContext";
import { IoMdClose, IoMdRemove, IoMdAdd } from 'react-icons/io';
import { PiTrash } from "react-icons/pi";

const CartTotal = () => {
    const { getTotalCartAmount } = useContext(CartContext);

    return (
        // Adjusted width using TailwindCSS utilities for larger screens
        // <div className="w-full md:w-3/4 lg:w-2/3 xl:max-w-[800px] p-4">

        //     <h1 className="text-2xl">Summary</h1>

        //     <div className="flex justify-between mb-2">
        //         <p>Subtotal</p>
        //         <p>${getTotalCartAmount()}</p>
        //     </div>

        //     <div className="flex justify-between mb-2">
        //         <p>Estimated Delivery & Handling</p>
        //         <p>$10</p>
        //     </div>

        //     <div className="flex justify-between mb-4">
        //         <p>Total</p>
        //         <p>${getTotalCartAmount() + 10}</p>
        //     </div>

        //     <button className="w-full">Checkout</button>

        // </div>
        <>
            <div className="px-10 text-2xl">Summary</div>
            <div className="flex">
            <div className="py-2 px-6 w-[500px] font-light min-h-[150px] flex">
                <div className='w-full flex flex-col pl-5 pt-5'>
                    <div className='flex justify-between mb-2'>
                        <p>Subtotal</p>
                        <p>${getTotalCartAmount()}</p>
                    </div>
                    <div className='flex justify-between mb-2 pt-2'>
                        <p>Estimated Delivery & Handling</p>
                        <p>$10</p>
                    </div>
                    <div className='flex justify-between mb-2 pt-2'>
                        <p>Taxes</p>
                        <p>---</p>
                    </div>
                    <div className="py-2 px-6 border-b border-gray-200"></div>
                    <div className='flex justify-between mb-2 pt-5'>
                        <p>Total</p>
                        <p className="font-semibold">${getTotalCartAmount() + 10}</p>
                    </div>
                    <div className="py-2 px-6 border-b border-gray-200"></div>
                    <button className='mt-10 w-full text-white bg-black hover:bg-gray-800 transition font-medium rounded-lg text-sm px-5 py-2.5 mr-2 mb-2 focus:outline-none'>Checkout</button>
                </div>
            </div>
        </div>
        </>
    );
}

export default CartTotal;
