import React from 'react';
import CartItems from '../components/CartItems';
import CartTotal from '../components/CartTotal';

const CartPage = () => {
  return (
    <div className='flex flex-col md:flex-row justify-center w-full'>
      {/* CartItems with responsive width, taking up remaining space */}
      <div className="w-full md:flex-1 px-4 md:px-0">
        <CartItems />
      </div>
      {/* CartTotal with specified width on larger screens */}
      <div className="w-full md:w-1/3 px-4 md:px-0 mt-4 md:mt-0">
        <CartTotal />
      </div>
    </div>
  );
}

export default CartPage;
