import React, { useContext } from "react";
import { CartContext } from "../components/CartContext";
import { IoMdRemove, IoMdAdd } from 'react-icons/io';
import { PiTrash } from "react-icons/pi";

const CartItems = () => {
    const { cart, removeFromCart, addToCart, removeAllFromCart } = useContext(CartContext);

    // Check if the cart is empty
    if (cart.length === 0) {
        return <div className="text-2xl pl-5">No items in your cart.</div>;
    }

    return (
        <div>
            <h1 className="text-2xl pl-5">Bag</h1>
            {cart.map((item) => (
                <div className="flex" key={item.id}>
                    <div className="py-2 px-6 border-b w-[700px] border-gray-200 font-light min-h-[150px] flex">
                        <div>
                            <img className="pt-5 pb-5" src={item.image} style={{ width: '200px', height: 'auto' }} alt={item.name} />
                        </div>
                        <div className='w-full flex flex-col pl-5 pt-5'>
                            <div className='font-semibold flex justify-between mb-2'>
                                <p>{item.name}</p>
                                <p>${item.new_price}</p>
                            </div>
                            <div className='flex gap-x-2 h-[36px] text-sm mt-2'>
                                <div className='flex flex-1 max-w-[100px] items-center h-full border text-primary font-medium'>
                                    <div onClick={() => removeFromCart(item.id)} className='flex-1 flex justify-center items-center cursor-pointer transition hover:text-black text-gray-500'>
                                        <IoMdRemove />
                                    </div>
                                    {/* Assuming you have a way to track and display quantity */}
                                    <div className='h-full flex justify-center items-center px-2'>Quantity</div>
                                    <div onClick={() => addToCart(item.id)} className='flex-1 h-full flex justify-center items-center cursor-pointer transition hover:text-black text-gray-500'>
                                        <IoMdAdd />
                                    </div>
                                </div>
                            </div>
                            <div className="mt-auto pb-5">
                                <div onClick={() => removeAllFromCart(item.id)} className='text-xl hover:cursor-pointer'>
                                    <PiTrash className='text-gray-500 transition hover:text-black'/>
                                </div>
                            </div>
                        </div>
                    </div>
                    <hr />
                </div>
            ))}
        </div>
    );
}

export default CartItems;
