import { createContext, useState, useEffect } from "react";
export const CartContext = createContext();

const CartProvider = ({ children }) => {
    const [cart, setCart] = useState([]);
    const [amount, setAmount] = useState(0);
    const [total, setTotal] = useState(0);

    useEffect(() => {
        const total = cart.reduce((accumulator, currentItem) => {
            return accumulator + currentItem.new_price * currentItem.amount;
        }, 0);
        setTotal(total);
    }, [cart]);

    useEffect(() => {
        if (cart) {
            const cartAmount = cart.reduce((accumulator, currentItem) => {
                return accumulator + currentItem.amount;
            }, 0);
            setAmount(cartAmount);
        }
    }, [cart]);

    const addToCart = (product, id) => {
        const newItem = { ...product, amount: 1 };

        // Make a POST request to the /addtocart endpoint with the newItem data
        fetch('/addtocart', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ product: newItem }),
        })
        .then(response => {
            if (response.status === 200) {
                // Product added to cart successfully, update the client-side cart
                setCart([...cart, newItem]);
            } else {
                // Handle error cases if necessary
                console.error('Failed to add product to cart');
            }
        })
        .catch(error => {
            // Handle network errors
            console.error('Network error:', error);
        });
    };

    const removeFromCart = (id) => {
        const newCart = cart.filter(item => {
            return item.id !== id;
        });
        setCart(newCart);
    };

    const increaseAmount = (id) => {
        const cartItem = cart.find(item => item.id === id);
        addToCart(cartItem, id);
    };

    const decreaseAmount = (id) => {
        const cartItem = cart.find(item => item.id === id);
        if (cartItem) {
            const newCart = cart.map(item => {
                if (item.id === id) {
                    return { ...item, amount: cartItem.amount - 1 };
                } else {
                    return item;
                }
            });
            setCart(newCart);
        }
        if (cartItem && cartItem.amount <= 1) {
            removeFromCart(id);
        }
    };

    return (
        <CartContext.Provider
            value={{
                cart,
                addToCart,
                removeFromCart,
                increaseAmount,
                decreaseAmount,
                amount,
                total,
            }}
        >
            {children}
        </CartContext.Provider>
    );
};

export default CartProvider;



